import React, { useState } from 'react';
import './index.css';

import AboutMe from './components/AboutMe';
import Education from './components/Education';
import Experience from './components/Experience';
import Skills from './components/Skills';
import Contact from './components/Contact';

import profilePic from './assets/profile.jpg'; // Make sure this image exists in your assets folder

function App() {
  const [darkMode, setDarkMode] = useState(false);

  const toggleDarkMode = () => {
    setDarkMode(prev => !prev);
  };

  return (
    <div className={darkMode ? "bg-gray-900 text-white" : "bg-gray-50 text-gray-900"}>
      <header className="flex items-center justify-between px-8 py-6 bg-blue-600 text-white shadow-md">
        <div className="flex items-center space-x-4">
          <img 
            src={profilePic} 
            alt="Profile" 
            className="w-12 h-12 rounded-full border-2 border-white shadow-sm"
          />
          <h1 className="text-3xl font-bold">My Portfolio</h1>
        </div>
        <button 
          onClick={toggleDarkMode}
          className="bg-white text-blue-600 px-4 py-1 rounded-md shadow hover:bg-gray-100 transition"
        >
          {darkMode ? 'Light Mode' : 'Dark Mode'}
        </button>
      </header>

      <main className="max-w-5xl mx-auto p-6 space-y-10">
        <AboutMe />
        <Education />
        <Experience />
        <Skills />
        <Contact />
      </main>

      <footer className="text-center py-4 text-sm text-gray-500">
        Â© {new Date().getFullYear()} My Portfolio. All rights reserved.
      </footer>
    </div>
  );
}

export default App;
