import React from 'react';
import { motion } from 'framer-motion';

const Experience = () => {
  return (
    <motion.section
      className="mt-8"
      initial={{ opacity: 0, y: 40 }}
      animate={{ opacity: 1, y: 0 }}
      transition={{ duration: 0.6 }}
    >
      <h2 className="text-2xl font-semibold mb-4">ðŸ’¼ Experience</h2>
      <div className="space-y-6">
        <div>
          <h3 className="font-bold text-lg">Frontend Developer</h3>
          <p className="text-sm text-gray-500 dark:text-gray-300">Tech Solutions Inc. | 2022 - Present</p>
          <p>Building dynamic React apps with clean UX and scalable architecture.</p>
        </div>
        <div>
          <h3 className="font-bold text-lg">Intern - Web Developer</h3>
          <p className="text-sm text-gray-500 dark:text-gray-300">CodeLab | Summer 2021</p>
          <p>Worked on HTML/CSS layout builds and assisted in WordPress migrations.</p>
        </div>
      </div>
    </motion.section>
  );
};

export default Experience;
